import React, { useState } from "react";
import { Link } from "react-router-dom";
import linkedInIcon from "../img/LinkedIn_icon.svg.png";
import gitHubIcon from "../img/githubwhite.png";

function Contacts() {
  const [hoveredHome, setHoveredHome] = useState(false);
  const [hoveredEmail, setHoveredEmail] = useState(false);

  const handleHoverHome = () => {
    setHoveredHome(!hoveredHome);
  };

  const handleHoverEmail = () => {
    setHoveredEmail(!hoveredEmail);
  };

  const handleCopyEmail = () => {
    const emailToCopy = "ruimsmelo@gmail.com";

    const textarea = document.createElement("textarea");
    textarea.value = emailToCopy;
    document.body.appendChild(textarea);

    textarea.select();
    textarea.setSelectionRange(0, 99999);

    document.execCommand("copy");

    document.body.removeChild(textarea);
  };

  return (
    <div className="allpages">
      <Link to="/">
        <h2
          onMouseEnter={handleHoverHome}
          onMouseLeave={handleHoverHome}
          className="home-button"
        >
          {hoveredHome ? "home" : "rui"}
        </h2>
      </Link>
      <h1>Let's work together!</h1>
      <p>
        Thank you for visiting my portfolio!
        <br />
        <br />
        I'm not just here to work for you. I'm here to work with you. My
        approach is all about fostering collaboration that optimizes the unique
        strengths each party brings.
        <br />
        <br />
        If you're in search of an open-minded collaborator, someone who values
        teamwork over ego, then let's skip the formalities and dive into how we
        can create something exceptional together.
        <br />
        <br />
        <br />
        Feel free to reach out through any platforms below:
        <br />
        <br />
        <br />
        <br />
        Email:
        <span
          className="email-span"
          onClick={handleCopyEmail}
          onMouseEnter={handleHoverEmail}
          onMouseLeave={handleHoverEmail}
        >
          {hoveredEmail
            ? "click to copy email to clipboard"
            : "ruimsmelo@gmail.com"}
        </span>
        <br />
        Phone:
        <br />
        +351 91 737 86 31
        <br />
        <br />
        <br />
        Connect with me on LinkedIn and GitHub to stay updated on my latest
        projects and professional adventures!
        <br />
        <br />
        <div className="icons-div">
          <Link
            to="https://www.linkedin.com/in/rui-ser%C3%B4dio-melo-a19715276/"
            target="_blank"
            className="icons-link"
          >
            <img src={linkedInIcon} className="icons" alt="LinkedIn" />
          </Link>
          <Link
            to="https://github.com/RuiMSMelo?tab=repositories"
            target="_blank"
            className="icons-link"
          >
            <img src={gitHubIcon} className="icons" alt="GitHub" />
          </Link>
        </div>
      </p>
    </div>
  );
}

export default Contacts;
