import { Link } from 'react-router-dom';
import oceanVideo from '../img/openoceanfinal.mp4';
import React, { useState } from 'react';

function Homepage() {
  const [hovered, setHovered] = useState(false);

  const handleHover = () => {
    setHovered(!hovered);
  };

  return (
    <div className='homepage-container'>
      <div className='video-container'>
        <video muted autoPlay loop className='video-background'>
          <source src={oceanVideo} type='video/mp4' />
        </video>
      </div>
      <div>
        <Link to='/'>
          <h2 className='home-button'>Home</h2>
        </Link>
      </div>
      <div className='homepage-text-container'>
        <h1 onMouseEnter={handleHover} onMouseLeave={handleHover}>
          {hovered ? 'About.' : 'Hello.'}
        </h1>
        <h1 onMouseEnter={handleHover} onMouseLeave={handleHover}>
          {hovered ? 'Projects' : "I'm"}
        </h1>
        <h1>I'm</h1>
        <h1>Rui Ser√¥dio Melo</h1>
      </div>
    </div>
  );
}

export default Homepage;
